import React, { useRef, useState, useCallback } from "react";
import { StyleSheet, View, Button, Image, Alert, Text } from "react-native";
import SignatureScreen from "react-native-signature-canvas";

const dataURL =
  "data:image/png;base64,";

const url =
  "data:image/png;base64,";

// Enhanced theme configuration
const theme = {
  colors: {
    background: "#f8f9fa",
    primary: "#007AFF",
    secondary: "#6c757d",
    success: "#28a745",
    danger: "#dc3545",
    warning: "#ffc107",
  },
  spacing: {
    xs: 4,
    sm: 8,
    md: 16,
    lg: 24,
    xl: 32,
  },
  borderRadius: 8,
};

const Sign = ({ onOK }: { onOK: (signature: string) => void }) => {
  const ref = useRef();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [penColor, setPenColor] = useState("#000000");

  const handleSignature = useCallback(
    (signature) => {
      try {
        console.log("Signature captured:", signature.length, "bytes");
        setError(null);
        onOK(signature);
      } catch (err) {
        console.error("Error handling signature:", err);
        setError("Failed to process signature");
      }
    },
    [onOK]
  );

  const handleClear = useCallback(() => {
    try {
      ref.current?.clearSignature();
      setError(null);
    } catch (err) {
      console.error("Error clearing signature:", err);
      setError("Failed to clear signature");
    }
  }, []);

  const handleConfirm = useCallback(() => {
    try {
      setIsLoading(true);
      ref.current?.readSignature();
    } catch (err) {
      console.error("Error reading signature:", err);
      setError("Failed to read signature");
      setIsLoading(false);
    }
  }, []);

  const undo = useCallback(() => {
    try {
      ref.current?.undo();
    } catch (err) {
      console.error("Error undoing:", err);
      setError("Failed to undo");
    }
  }, []);

  const erase = useCallback(() => {
    try {
      ref.current?.erase();
    } catch (err) {
      console.error("Error switching to erase mode:", err);
      setError("Failed to switch to erase mode");
    }
  }, []);

  const draw = useCallback(() => {
    try {
      ref.current?.draw();
    } catch (err) {
      console.error("Error switching to draw mode:", err);
      setError("Failed to switch to draw mode");
    }
  }, []);

  const changeColor = useCallback((color) => {
    try {
      ref.current?.changePenColor(color);
      setPenColor(color);
      setError(null);
    } catch (err) {
      console.error("Error changing pen color:", err);
      setError("Failed to change pen color");
    }
  }, []);

  const handleError = useCallback((err) => {
    console.error("Signature pad error:", err);
    setError(err.message || "An error occurred");
    setIsLoading(false);
  }, []);

  const handleLoadEnd = useCallback(() => {
    console.log("SignatureScreen loaded successfully");
    setIsLoading(false);
    setError(null);
  }, []);

  const handleEmpty = useCallback(() => {
    setIsLoading(false);
    Alert.alert("Empty Signature", "Please draw a signature before saving.");
  }, []);

  const style = `.m-signature-pad--footer {display: none; margin: 0px;}`;

  return (
    <View style={styles.container}>
      <View style={styles.box}>
        <SignatureScreen
          ref={ref}
          onOK={handleSignature}
          style={styles.signature}
          webviewContainerStyle={styles.signature}
          webStyle={`
                .m-signature-pad {
                  background-color: white;
                  flex: 1;
                  box-shadow: none;
                  border-radius: 10px;
                }

                `}
          backgroundColor={"white"}
          onError={handleError}
          onLoadEnd={handleLoadEnd}
          onEmpty={handleEmpty}
          penColor={penColor}
          // Example of using webviewProps to customize WebView behavior
          webviewProps={
            {
              // Override default WebView settings
              // cacheEnabled: false, // Disable cache for this specific use case
              // allowsFullscreenVideo: false,
              // decelerationRate: 0.998,
              // Add any other WebView props as needed
            }
          }
          // dataURL={url}
        />
      </View>

      {error && (
        <View style={styles.errorContainer}>
          <Text style={styles.errorText}>{error}</Text>
        </View>
      )}

      <View style={styles.controlsContainer}>
        <View style={styles.row}>
          <Button
            title="Undo"
            onPress={undo}
            color={theme.colors.secondary}
            disabled={isLoading}
          />
          <Button
            title="Draw"
            onPress={draw}
            color={theme.colors.primary}
            disabled={isLoading}
          />
          <Button
            title="Erase"
            onPress={erase}
            color={theme.colors.warning}
            disabled={isLoading}
          />
        </View>

        <View style={styles.row}>
          <Button
            title="Red"
            onPress={() => changeColor("#dc3545")}
            color={theme.colors.danger}
            disabled={isLoading}
          />
          <Button
            title="Blue"
            onPress={() => changeColor("#007AFF")}
            color={theme.colors.primary}
            disabled={isLoading}
          />
          <Button
            title="Black"
            onPress={() => changeColor("#000000")}
            color={theme.colors.secondary}
            disabled={isLoading}
          />
        </View>

        <View style={styles.row}>
          <Button
            title="Clear"
            onPress={handleClear}
            color={theme.colors.secondary}
            disabled={isLoading}
          />
          <Button
            title={isLoading ? "Processing..." : "Save"}
            onPress={handleConfirm}
            color={theme.colors.success}
            disabled={isLoading}
          />
        </View>
      </View>
    </View>
  );
};

export default Sign;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: theme.spacing.md,
    backgroundColor: theme.colors.background,
  },
  box: {
    height: 400,
    marginBottom: theme.spacing.md,
    borderRadius: theme.borderRadius,
    borderWidth: 1,
    borderColor: "#e0e0e0",
    overflow: "hidden",
  },
  signature: {
    backgroundColor: "white",
    flex: 1,
  },
  controlsContainer: {
    gap: theme.spacing.sm,
  },
  row: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: theme.spacing.sm,
  },
  errorContainer: {
    backgroundColor: theme.colors.danger,
    padding: theme.spacing.sm,
    borderRadius: theme.borderRadius,
    marginBottom: theme.spacing.md,
  },
  errorText: {
    color: "white",
    textAlign: "center",
    fontWeight: "500",
  },
});
